import React, { useState } from "react";
import SetupCard from "./SetupCard";
import ResultsCard from "./ResultCard";
import QuestionCard from "./QuestionCard";

import "./styles.css";

const QUESTION_BANK = [
  {
    prompt: "What is the capital of France?",
    options: ["Berlin", "Madrid", "Paris", "Rome"],
    answerIndex: 2
  },
  // ... your full question bank goes here
];

export default function QuizApp() {
  const [phase, setPhase] = useState("setup");
  const [questions, setQuestions] = useState([]);
  const [index, setIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [numQuestions, setNumQuestions] = useState(0); // track chosen number

  // start quiz with selected number of questions
  const startQuiz = (questionsCount) => {
    const count = questionsCount || numQuestions; // use previous if undefined
    setNumQuestions(count); // save for "Play Again"
    setQuestions([...QUESTION_BANK].sort(() => Math.random() - 0.5).slice(0, count));
    setPhase("quiz");
    setIndex(0);
    setScore(0);
  };

  const submitAnswer = (choiceIndex) => {
    if (choiceIndex === questions[index].answerIndex) {
      setScore((s) => s + 1);
    }
    if (index + 1 < questions.length) {
      setIndex((i) => i + 1);
    } else {
      setPhase("results");
    }
  };

  const resetAll = () => {
    setPhase("setup");
    setQuestions([]);
    setIndex(0);
    setScore(0);
    setNumQuestions(0);
  };

  return (
    <div className="quiz-app">
      {phase === "setup" && (
        <SetupCard
          onStart={startQuiz}
          maxQuestions={QUESTION_BANK.length}
        />
      )}

      {phase === "quiz" && questions.length > 0 && (
        <QuestionCard
          question={questions[index]}
          index={index}
          total={questions.length}
          onAnswer={submitAnswer}
        />
      )}

      {phase === "results" && (
        <ResultsCard
          score={score}
          total={questions.length}
          questions={questions}
          onRestart={() => startQuiz()} // will use previous numQuestions
          onReset={resetAll}
        />
      )}
    </div>
  );
}
