// src/components/Navbar.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem("token");

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>QuizApp</div>
      <div style={styles.links}>
        {isLoggedIn ? (
          <>
            <Link style={styles.link} to="/">Dashboard</Link>
            <Link style={styles.link} to="/past-quizzes">Past Quizzes</Link>
            <Link style={styles.link} to="/upcoming-quizzes">Upcoming Quizzes</Link>
            <Link style={styles.link} to="/profile">Profile</Link>
            <Link style={styles.link} to="/leaderboard">Leaderboard</Link>
            <Link style={styles.link} to="/settings">Settings</Link>
            <button style={styles.logoutBtn} onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <Link style={styles.link} to="/login">Login</Link>
            <Link style={styles.link} to="/signup">Signup</Link>
            <Link style={styles.link} to="/settings">Settings</Link>
          </>
        )}
      </div>
    </nav>
  );
}

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#2575fc",
    color: "#fff",
    flexWrap: "wrap",
  },
  logo: {
    fontWeight: "bold",
    fontSize: "1.2em",
  },
  links: {
    display: "flex",
    gap: "15px",
    flexWrap: "wrap",
    alignItems: "center",
  },
  link: {
    color: "#fff",
    textDecoration: "none",
    fontWeight: "500",
  },
  logoutBtn: {
    backgroundColor: "#ff4d4f",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
    color: "#fff",
    fontWeight: "bold",
  },
};
