import React, { useState } from "react";

export default function Settings({ onStart, maxQuestions }) {
  const [numQuestions, setNumQuestions] = useState(5);
  const [timePerQuestion, setTimePerQuestion] = useState(10);

  const handleStart = () => {
    onStart(numQuestions, timePerQuestion);
  };

  return (
    <div className="card">
      <h2>Quiz Settings</h2>
      <p>Configure your quiz before starting:</p>

      {/* Number of questions */}
      <label htmlFor="questions">Number of questions:</label>
      <select
        id="questions"
        value={numQuestions}
        onChange={(e) => setNumQuestions(Number(e.target.value))}
      >
        {[5, 10, 15, 20, 25].filter(n => n <= maxQuestions).map((num) => (
          <option key={num} value={num}>{num}</option>
        ))}
      </select>

      {/* Time per question */}
      <label htmlFor="time">Time per question (seconds):</label>
      <select
        id="time"
        value={timePerQuestion}
        onChange={(e) => setTimePerQuestion(Number(e.target.value))}
      >
        {[5, 10, 15, 20].map((sec) => (
          <option key={sec} value={sec}>{sec}s</option>
        ))}
      </select>

      <button onClick={handleStart}>Start Quiz</button>
    </div>
  );
}
