import React, { useState, useEffect } from "react";

function PastQuizzes() {
  const [pastQuizzes, setPastQuizzes] = useState([]);

  useEffect(() => {
    // Generate mock quizzes for the current year
    const currentYear = new Date().getFullYear();
    const today = new Date();

    // Example dataset of 20 quizzes spread across the year
    const generatedQuizzes = Array.from({ length: 20 }, (_, i) => {
      const randomDay = Math.floor(Math.random() * 300); // 300 days into the year
      const quizDate = new Date(currentYear, 0, 1 + randomDay);
      return {
        id: i + 1,
        title: `Aptitude Quiz #${i + 1}`,
        date: quizDate,
        score: Math.floor(Math.random() * 100),
        total: 100,
      };
    }).filter((quiz) => quiz.date <= today); // Only past quizzes

    // Sort by most recent first
    generatedQuizzes.sort((a, b) => b.date - a.date);

    setPastQuizzes(generatedQuizzes);
  }, []);

  return (
    <div style={styles.container}>
      <h2>Past Quizzes</h2>
      <p>Showing all quizzes from Jan 1 to today.</p>

      {pastQuizzes.length === 0 ? (
        <p>No quizzes found yet.</p>
      ) : (
        <div style={styles.quizList}>
          {pastQuizzes.map((quiz) => (
            <div key={quiz.id} style={styles.quizCard}>
              <h3>{quiz.title}</h3>
              <p>
                üìÖ <strong>Date:</strong>{" "}
                {quiz.date.toLocaleDateString("en-GB")}
              </p>
              <p>
                üèÜ <strong>Score:</strong> {quiz.score}/{quiz.total}
              </p>
              <p>
                {quiz.score >= 70 ? "‚úÖ Passed" : quiz.score >= 40 ? "‚ö†Ô∏è Average" : "‚ùå Failed"}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  quizList: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "20px",
    marginTop: "20px",
  },
  quizCard: {
    backgroundColor: "#f9f9f9",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    textAlign: "center",
    transition: "transform 0.2s ease",
  },
  quizCardHover: {
    transform: "scale(1.02)",
  },
};

export default PastQuizzes;
