import React, { useEffect, useState } from "react";

function UpcomingQuizzes() {
  const [upcomingQuizzes, setUpcomingQuizzes] = useState([]);

  useEffect(() => {
    const today = new Date();
    const currentYear = today.getFullYear();

    const quizzes = [];

    // Generate 3 quizzes per month from current month ‚Üí December
    for (let month = today.getMonth(); month < 12; month++) {
      for (let i = 0; i < 3; i++) {
        const randomDay = Math.floor(Math.random() * 25) + 1; // between 1‚Äì25
        const randomHour = Math.floor(Math.random() * 12) + 8; // between 8AM‚Äì8PM
        const randomMinute = Math.floor(Math.random() * 60);

        const quizDate = new Date(currentYear, month, randomDay, randomHour, randomMinute);

        // Skip if it's in the past
        if (quizDate <= today) continue;

        quizzes.push({
          id: `${month + 1}-${i + 1}`,
          title: `Aptitude Quiz ${month + 1}-${i + 1}`,
          date: quizDate,
          category: ["Logic", "Math", "Verbal"][i % 3],
          duration: "30 mins",
        });
      }
    }

    // Sort ascending by date
    quizzes.sort((a, b) => a.date - b.date);

    setUpcomingQuizzes(quizzes);
  }, []);

  const formatDate = (date) =>
    date.toLocaleDateString("en-GB", {
      day: "2-digit",
      month: "short",
      year: "numeric",
    });

  const formatTime = (date) =>
    date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });

  return (
    <div style={styles.container}>
      <h2>Upcoming Quizzes</h2>
      <p>Get ready! Here are your scheduled quizzes for the rest of the year üìÖ</p>

      {upcomingQuizzes.length === 0 ? (
        <p>No upcoming quizzes found.</p>
      ) : (
        <div style={styles.quizList}>
          {upcomingQuizzes.map((quiz) => {
            const isToday =
              new Date().toDateString() === quiz.date.toDateString();

            return (
              <div key={quiz.id} style={styles.quizCard}>
                <h3>{quiz.title}</h3>
                <p>
                  üß© <strong>Category:</strong> {quiz.category}
                </p>
                <p>
                  üìÖ <strong>Date:</strong> {formatDate(quiz.date)}
                </p>
                <p>
                  ‚è∞ <strong>Time:</strong> {formatTime(quiz.date)}
                </p>
                <p>
                  ‚è≥ <strong>Duration:</strong> {quiz.duration}
                </p>
                <p
                  style={{
                    color: isToday ? "#4CAF50" : "#007BFF",
                    fontWeight: "bold",
                  }}
                >
                  {isToday ? "üî• Happening Today!" : "üïí Upcoming"}
                </p>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  quizList: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "20px",
    marginTop: "20px",
  },
  quizCard: {
    backgroundColor: "#f9f9f9",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
    transition: "transform 0.2s ease, box-shadow 0.2s ease",
    textAlign: "center",
  },
};

export default UpcomingQuizzes;
