  return (
    <div style={{ padding: "40px", textAlign: "center" }}>
      <h1>Privacy Policy</h1>
      <p>We respect your privacy. All your data is safe with us.</p>
      <p>We at QuizApp value your privacy. This is a student project, and hereâ€™s what you need to know:</p>
      <p> What we collect: Only basic info like your name, email (if you create an account), and quiz results. <br />
        How we use it: To run the app, track your scores, and improve the experience.<br />
        Sharing info: We do not sell or share your personal info with anyone.<br />
        Security: We try to keep your info safe, but no system is 100% secure.<br />
        Cookies: We use cookies to make the app work better for you.</ p>
        <p> By using the app, you agree to this privacy policy.</p>
        <p> If you have questions, contact us.</p>
    </div>
  );
}
