import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import QuizApp from "./components/QuizApp";
import ResultsCard from "./components/ResultCard";
import QuestionCard from "./components/QuestionCard";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Profile from "./components/Profile";
import Settings from "./components/Settings";
import PastQuizzes from "./components/PastQuizzes";
import UpcomingQuizzes from "./components/UpcomingQuizzes";

import "./App.css";

export default function App() {
  return (
    <Router>
      <div style={styles.appContainer}>
        {/* Navbar always visible */}
        <Navbar />

        {/* Main content area grows to fill space */}
        <div style={styles.content}>
          <Routes>
            <Route path="/" element={<QuizApp />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/results" element={<ResultsCard />} />
            <Route path="/question" element={<QuestionCard />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="/past-quizzes" element={<PastQuizzes />} />
            <Route path="/upcoming-quizzes" element={<UpcomingQuizzes />} />
            <Route path="*" element={<h2>Page Not Found</h2>} />
          </Routes>
        </div>

        {/* Footer pinned at bottom */}
        <Footer />
      </div>
    </Router>
  );
}

const styles = {
  appContainer: {
    display: "flex",
    flexDirection: "column",
    minHeight: "100vh", // Full viewport height
  },
  content: {
    flex: 1, // This makes the middle section take up remaining space
    padding: "20px",
  },
};
