import React, { useState } from "react";
import SetupCard from "./SetupCard";
import ResultsCard from "./ResultCard"; 
import QuestionCard from "./QuestionCard";
import "./styles.css";

const QUESTION_BANK = [
  { id: "m1", category: "math", difficulty: "easy", prompt: "What is 12 + 15?", options: ["25", "26", "27", "28"], answerIndex: 2 },
  { id: "l1", category: "logic", difficulty: "easy", prompt: "Find the next number: 2, 4, 8, 16, ?", options: ["18", "24", "32", "64"], answerIndex: 2 },
  { id: "v1", category: "verbal", difficulty: "easy", prompt: "Choose the synonym of 'lucid'", options: ["clear", "confused", "dim", "opaque"], answerIndex: 0 },
  { id: "m2", category: "math", difficulty: "medium", prompt: "What is the square root of 144?", options: ["10", "11", "12", "13"], answerIndex: 2 },
  { id: "l2", category: "logic", difficulty: "medium", prompt: "If all Bloops are Razzies and all Razzies are Lazzies, are all Bloops definitely Lazzies?", options: ["Yes", "No"], answerIndex: 0 },
  { id: "v2", category: "verbal", difficulty: "medium", prompt: "Choose the antonym of 'abundant'", options: ["plentiful", "scarce", "ample", "bountiful"], answerIndex: 1 },
  { id: "m3", category: "math", difficulty: "hard", prompt: "What is the derivative of x^3?", options: ["2x", "3x^2", "x^2", "3x"], answerIndex: 1 },
  { id: "l3", category: "logic", difficulty: "hard", prompt: "A train leaves the station at 3 PM traveling at 60 mph. Another train leaves the same station at 4 PM traveling at 80 mph. When will the second train catch up to the first?", options: ["5 PM", "6 PM", "7 PM", "8 PM"], answerIndex: 2 },
  { id: "v3", category: "verbal", difficulty: "hard", prompt: "What is the meaning of the word 'obfuscate'?", options: ["to clarify", "to confuse", "to simplify", "to illuminate"], answerIndex: 1 },
  { id: "m4", category: "math", difficulty: "easy", prompt: "What is 7 * 6?", options: ["42", "36", "48", "40"], answerIndex: 0 },
  { id: "l4", category: "logic", difficulty: "easy", prompt: "What comes next in the sequence: A, C, E, G, ?", options: ["H", "I", "J", "K"], answerIndex: 1 },
  { id: "v4", category: "verbal", difficulty: "easy", prompt: "Choose the synonym of 'happy'", options: ["sad", "joyful", "angry", "upset"], answerIndex: 1 },
  { id: "m5", category: "math", difficulty: "medium", prompt: "What is 15% of 200?", options: ["25", "30", "35", "40"], answerIndex: 1 },
  { id: "l5", category: "logic", difficulty: "medium", prompt: "If some Wugs are Zogs and some Zogs are Pogs, are some Wugs definitely Pogs?", options: ["Yes", "No"], answerIndex: 1 },  
  { id: "v5", category: "verbal", difficulty: "medium", prompt: "Choose the antonym of 'ancient'", options: ["old", "modern", "historic", "antique"], answerIndex: 1 },
  { id: "m6", category: "math", difficulty: "hard", prompt: "What is the integral of 2x dx?", options: ["x^2 + C", "2x^2 + C", "x + C", "x^2/2 + C"], answerIndex: 0 },
  { id: "l6", category: "logic", difficulty: "hard", prompt: "A farmer has 17 sheep and all but 9 die. How many are left alive?", options: ["8", "9", "17", "0"], answerIndex: 1 },
  { id: "v6", category: "verbal", difficulty: "hard", prompt: "What is the meaning of the word 'ephemeral'?", options: ["lasting forever", "short-lived", "eternal", "permanent"], answerIndex: 1 },
  { id: "m7", category: "math", difficulty: "easy", prompt: "What is 9 - 4?", options: ["3", "4", "5", "6"], answerIndex: 2 },
  { id: "l7", category: "logic", difficulty: "easy", prompt: "What comes next in the sequence: 1, 3, 5, 7, ?", options: ["8", "9", "10", "11"], answerIndex: 1 },
  { id: "v7", category: "verbal", difficulty: "easy", prompt: "Choose the synonym of 'fast'", options: ["quick", "slow", "sluggish", "lethargic"], answerIndex: 0 },
  { id: "m8", category: "math", difficulty: "medium", prompt: "What is the value of x if 2x + 3 = 11?", options: ["3", "4", "5", "6"], answerIndex: 1 },
  { id: "l8", category: "logic", difficulty: "medium", prompt: "If all Fizzles are Bizzles and some Bizzles are Dizzles, are some Fizzles definitely Dizzles?", options: ["Yes", "No"], answerIndex: 1 },
  { id: "v8", category: "verbal", difficulty: "medium", prompt: "Choose the antonym of 'brave'", options: ["courageous", "fearful", "valiant", "heroic"], answerIndex: 1 },
  { id: "m9", category: "math", difficulty: "hard", prompt: "What is the solution to the equation x^2 - 4x + 4 = 0?", options: ["x = 2", "x = -2", "x = 4", "x = -4"], answerIndex: 0 },
  { id: "l9", category: "logic", difficulty: "hard", prompt: "A clock shows the time as 3:15. What is the angle between the hour and the minute hands?", options: ["7.5 degrees", "30 degrees", "45 degrees", "90 degrees"], answerIndex: 0 },
  { id: "v9", category: "verbal", difficulty: "hard", prompt: "What is the meaning of the word 'quintessential'?", options: ["typical", "atypical", "unusual", "extraordinary"], answerIndex: 0 },
  { id: "m10", category: "math", difficulty: "easy", prompt: "What is 5 + 7?", options: ["11", "12", "13", "14"], answerIndex: 1 },
  { id: "l10", category: "logic", difficulty: "easy", prompt: "What comes next in the sequence: B, D, F, H, ?", options: ["I", "J", "K", "L"], answerIndex: 1 },
  { id: "v10", category: "verbal", difficulty: "easy", prompt: "Choose the synonym of 'smart'", options: ["intelligent", "dumb", "stupid", "foolish"], answerIndex: 0 }
];

export default function QuizApp() {
  const [phase, setPhase] = useState("setup");
  const [questions, setQuestions] = useState([]);
  const [index, setIndex] = useState(0);
  const [score, setScore] = useState(0);

  const startQuiz = (numQuestions) => {
  setQuestions([...QUESTION_BANK].sort(() => Math.random() - 0.5).slice(0, numQuestions));
  setPhase("quiz");
  setIndex(0);
  setScore(0);
};


  const submitAnswer = (choiceIndex) => {
    if (choiceIndex === questions[index].answerIndex) {
      setScore((s) => s + 1);
    }
    if (index + 1 < questions.length) {
      setIndex((i) => i + 1);
    } else {
      setPhase("results");
    }
  };

  const resetAll = () => {
    setPhase("setup");
    setQuestions([]);
    setIndex(0);
    setScore(0);
  };

  return (
    <div className="quiz-app">
      {phase === "setup" && <SetupCard onStart={startQuiz} />}
      {phase === "quiz" && questions.length > 0 && (
        <QuestionCard
          question={questions[index]}
          index={index}
          total={questions.length}
          onAnswer={submitAnswer}
        />
      )}

      {phase === "results" && (
  <ResultsCard
    score={score}
    total={questions.length}
    questions={questions} 
    onRestart={startQuiz}
    onReset={resetAll}
  />
)}
    </div>
  );
}
