import React, { useState } from "react";

export default function Settings({ onStart, maxQuestions, userProfile, onUpdateProfile }) {
  // Quiz settings
  const [numQuestions, setNumQuestions] = useState(5);
  const [timePerQuestion, setTimePerQuestion] = useState(10);

  // Profile fields
  const [username, setUsername] = useState(userProfile?.username || "");
  const [name, setName] = useState(userProfile?.name || "");
  const [email, setEmail] = useState(userProfile?.email || "");
  const [phone, setPhone] = useState(userProfile?.phone || "");

  const handleStart = () => {
    onStart(numQuestions, timePerQuestion);
  };

  const handleProfileUpdate = () => {
    onUpdateProfile({ username, name, email, phone });
    alert("Profile updated!");
  };

  return (
    <div className="card">
      <h2>Settings</h2>

      {/* Quiz Settings */}
      <div style={{ marginBottom: "25px" }}>
        <h3>Quiz Settings</h3>
        <label>Number of Questions:</label>
        <select value={numQuestions} onChange={(e) => setNumQuestions(Number(e.target.value))}>
          {[5, 10, 15, 20, 25].filter(n => n <= maxQuestions).map(num => (
            <option key={num} value={num}>{num}</option>
          ))}
        </select>

        <label>Time per Question (seconds):</label>
        <select value={timePerQuestion} onChange={(e) => setTimePerQuestion(Number(e.target.value))}>
          {[5, 10, 15, 20].map(sec => (
            <option key={sec} value={sec}>{sec}s</option>
          ))}
        </select>

        <button onClick={handleStart} style={{ marginTop: "10px" }}>Start Quiz</button>
      </div>

      {/* Profile Settings */}
      <div style={{ marginBottom: "25px" }}>
        <h3>Edit Profile</h3>
        <label>Username:</label>
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />

        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />

        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />

        <label>Phone:</label>
        <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />

        <button onClick={handleProfileUpdate} style={{ marginTop: "10px" }}>Update Profile</button>
      </div>

      {/* About Developer */}
      <div>
        <h3>About the Developer</h3>
        <p>
          Developed by Jane Doe. Passionate about building interactive educational apps.
          Contact: jane.doe@example.com
        </p>
      </div>
    </div>
  );
}
