// src/components/Navbar.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Check login status on mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/login");
  };

  const getLinkStyle = (requiresLogin = false) => ({
    color: requiresLogin && !isLoggedIn ? "#aaa" : "#fff",
    textDecoration: "none",
    fontWeight: requiresLogin && !isLoggedIn ? "400" : "500",
    pointerEvents: requiresLogin && !isLoggedIn ? "none" : "auto",
    cursor: requiresLogin && !isLoggedIn ? "default" : "pointer",
  });

  return (
    <nav style={styles.navbar}>
      {/* LEFT SIDE */}
      <div style={styles.leftSection}>
        <div style={styles.logo}>
          <Link to="/" style={{ color: "#fff", textDecoration: "none" }}>
            QuizApp
          </Link>
        </div>

        {/* Protected Links beside QuizApp */}
        <div style={styles.leftLinks}>
          <Link style={getLinkStyle(true)} to="/dashboard">
            Dashboard
          </Link>
          <Link style={getLinkStyle(true)} to="/leaderboard">
            Leaderboard
          </Link>
          <Link style={getLinkStyle(true)} to="/past-quizzes">
            Past Quizzes
          </Link>
          <Link style={getLinkStyle(true)} to="/upcoming-quizzes">
            Upcoming Quizzes
          </Link>
          <Link style={getLinkStyle(true)} to="/profile">
            Profile
          </Link>
        </div>
      </div>

      {/* RIGHT SIDE */}
      <div style={styles.rightSection}>
        {!isLoggedIn && (
          <>
            <Link style={getLinkStyle()} to="/login">
              Login
            </Link>
            <Link style={getLinkStyle()} to="/signup">
              Signup
            </Link>
          </>
        )}
        <Link style={getLinkStyle()} to="/settings">
          Settings
        </Link>
        {isLoggedIn && (
          <button style={styles.logoutBtn} onClick={handleLogout}>
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#2575fc",
    color: "#fff",
    flexWrap: "wrap",
    position: "sticky",
    top: 0,
    zIndex: 100,
  },
  leftSection: {
    display: "flex",
    alignItems: "center",
    gap: "25px",
  },
  logo: {
    fontWeight: "bold",
    fontSize: "1.3em",
  },
  leftLinks: {
    display: "flex",
    alignItems: "center",
    gap: "15px",
    flexWrap: "wrap",
  },
  rightSection: {
    display: "flex",
    alignItems: "center",
    gap: "15px",
    flexWrap: "wrap",
  },
  logoutBtn: {
    backgroundColor: "#ff4d4f",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
    color: "#fff",
    fontWeight: "bold",
  },
};
